<script>
    export default {
        data(){
            return {}
        },
        mounted(){
            document.addEventListener('DOMContentLoaded', function (event) {
                // array with texts to type in typewriter
                var dataText = ["Pemilik Bisnis Bisa Pantau", "Pemilik Bisnis Bisa Jalan-Jalan",
                    "Pemilik Bisnis Senang", "Pemilik Bisnis Berkembang", "Perusahaan Bisa Hemat",
                    "Perusahaan Lebih Efisien", "Perusahaan Berkembang"
                ];

                // type one text in the typwriter
                // keeps calling itself until the text is finished
                function typeWriter(text, i, fnCallback) {
                    // chekc if text isn't finished yet
                    if (i < (text.length)) {
                        // add next character to h1
                        document.querySelector("h1").innerHTML = text.substring(0, i + 1) +
                            '<span aria-hidden="true"></span>';

                        // wait for a while and call this function again for next character
                        setTimeout(function () {
                            typeWriter(text, i + 1, fnCallback)
                        }, 100);
                    }
                    // text finished, call callback if there is a callback function
                    else if (typeof fnCallback == 'function') {
                        // call callback after timeout
                        setTimeout(fnCallback, 5000);
                    }
                }
                // start a typewriter animation for a text in the dataText array
                function StartTextAnimation(i) {
                    if (typeof dataText[i] == 'undefined') {
                        setTimeout(function () {
                            StartTextAnimation(0);
                        }, 0);
                    }
                    // check if dataText[i] exists
                    if (i < dataText[i].length) {
                        // text exists! start typewriter animation
                        typeWriter(dataText[i], 0, function () {
                            // after callback (and whole text has been animated), start next text
                            StartTextAnimation(i + 1);
                        });
                    }
                }
                // start the text animation
                StartTextAnimation(0);
            });
        },
        methods: {
            scrollFunction() {
                if (document.body.scrollTop > 200 || document.documentElement.scrollTop > 200) {
                    document.getElementById("navbar").style.backgroundColor = "#1F1F2B";
                } else {
                    document.getElementById("navbar").style.backgroundColor = "transparent";
                }
                if (document.body.scrollTop > 580 || document.documentElement.scrollTop > 580) {
                    document.getElementById("topBtn").style.display = "unset";
                } else {
                    document.getElementById("topBtn").style.display = "none";
                }
                if (document.body.scrollTop > 400 || document.documentElement.scrollTop > 400) {
                    document.getElementById("cobagratis").style.display = "unset";
                } else {
                    document.getElementById("cobagratis").style.display = "none";
                }
            },

            topFunction() {
                document.body.scrollTop = 0;
                document.documentElement.scrollTop = 0;
