          <h2 class="auth-description text-center" style="color: #34b1e8">
            Selamat Datang
          </h2>

          <div class="">
            <p>Silahkan lakukan registrasi terlebih dahulu</p>

            <form @submit.prevent="register" class="mt-5">
              <div class="auth-credentials m-b-xxl">
                <div class="input-icons">
                  <div class="form-group">
                    <label for="" class="form-label">Nama Perusahaan</label>
                    <input
                      v-model="data.nama_perusahaan"
                      :class="
                        validation.nama_perusahaan.status ? 'is-invalid' : ''
                      "
                      type="text"
                      @change="validation.nama_perusahaan.status = false"
                      class="form-control"
                      placeholder="Nama Perusahaan"
                      required
                    />
                    <span
                      class="invalid-feedback"
                      v-if="validation.nama_perusahaan.status"
                      role="alert"
                    >
                      <strong>{{ validation.nama_perusahaan.message }}</strong>
                    </span>
                  </div>

                  <div class="form-group">
                    <label for="" class="form-label">Bidang Usaha</label>
                    <select
                      v-model="data.bidang_usaha"
                      :class="
                        validation.bidang_usaha.status ? 'is-invalid' : ''
                      "
                      @change="validation.bidang_usaha.status = false"
                      class="form-select"
                      aria-label="Default select example"
                      required
                    >
                      <option value="" disabled selected>
                        -- Pilih Bidang Usaha --
                      </option>
                      <option
                        v-for="(val, index) in bidang_usaha"
                        :key="index"
                        v-bind:value="val.id_m_company_industri"
                      >
                        {{ val.m_company_industriFields }}
                      </option>
                    </select>
                    <span
                      class="invalid-feedback"
                      v-if="validation.bidang_usaha.status"
                      role="alert"
                    >
                      <strong>{{ validation.bidang_usaha.message }}</strong>
                    </span>
                  </div>

                  <div class="form-group">
                    <label for="" class="form-label">No. Telp</label>
                    <input
                      type="tel"
                      pattern="[0-9]{12-13}"
                      min="0"
                      v-model="data.no_telp"
                      :class="validation.no_telp.status ? 'is-invalid' : ''"
                      @change="validation.no_telp.status = false"
                      class="form-control"
                      placeholder="No. telp"
                      required
                    />
                    <span
                      class="invalid-feedback"
                      v-if="validation.no_telp.status"
                      role="alert"
                    >
                      <strong>{{ validation.no_telp.message }}</strong>
                    </span>
                  </div>

                  <div class="form-group">
                    <label for="" class="form-label">Email</label>
                    <input
                      type="email"
                      v-model="data.email"
                      class="form-control"
                      :class="validation.email.status ? 'is-invalid' : ''"
                      placeholder="email"
                      @change="validation.email.status = false"
                      required
                    />
                    <span
                      class="invalid-feedback"
                      v-if="validation.email.status"
                      role="alert"
                    >
                      <strong>{{ validation.email.message }}</strong>
                    </span>
                  </div>

                  <div class="form-group">
                    <label for="" class="form-label">Total Anggota</label>
                    <input
                      type="number"
                      v-model="data.total_anggota"
                      :class="
                        validation.total_anggota.status ? 'is-invalid' : ''
                      "
                      min="0"
                      @change="validation.total_anggota.status = false"
                      class="form-control"
                      placeholder="Total Anggota"
                      required
                    />
                    <span
                      class="invalid-feedback"
                      v-if="validation.total_anggota.status"
                      role="alert"
                    >
                      <strong>{{ validation.total_anggota.message }}</strong>
                    </span>
                  </div>

                  <div class="form-group">
                    <label for="" class="form-label">Timezone</label>
                    <select
                      v-model="data.timezone"
                      class="form-select"
                      aria-label="Default select example"
                      required
                    >
                      <option value="" disabled selected>
                        -- Pilih Timezone --
                      </option>
                      <option value="WIB">WIB</option>
                      <option value="WITA">WITA</option>
                      <option value="WIT">WIT</option>
                    </select>
                  </div>

                  <div class="form-group">
                    <label for="" class="form-label">Password</label>
                    <input
                      type="password"
                      id="password"
                      class="form-control"
                      :class="validation.password.status ? 'is-invalid' : ''"
                      @change="validation.password.status = false"
                      v-model="data.password"
                      placeholder="&#9679;&#9679;&#9679;&#9679;&#9679;&#9679;&#9679;&#9679;"
                      required
                    />
                    <div
                      style="
                        float: right;
                        position: relative;
                        margin-top: -30px;
                        margin-right: 20px;
                      "
                    >
                      <img
                        src="/assets/images/icons/eye.svg"
                        alt=""
                        style="cursor: pointer; width: 15px; height: auto"
                        id="showPassword"
                      />
                    </div>
                    <span
                      class="invalid-feedback"
                      v-if="validation.password.status"
                      role="alert"
                    >
                      <strong>{{ validation.password.message }}</strong>
                    </span>
                  </div>

                  <div class="form-group">
                    <label for="" class="form-label">Konfirmasi Password</label>
                    <input
                      type="password"
                      id="password_confirmation"
                      class="form-control"
                      :class="
                        validation.password_confirmation.status
                          ? 'is-invalid'
                          : ''
                      "
                      @change="validation.password_confirmation.status = false"
                      v-model="data.password_confirmation"
                      placeholder="&#9679;&#9679;&#9679;&#9679;&#9679;&#9679;&#9679;&#9679;"
                      required
                    />
                    <div
                      style="
                        float: right;
                        position: relative;
                        margin-top: -30px;
                        margin-right: 20px;
                      "
                    >
                      <img
                        src="/assets/images/icons/eye.svg"
                        alt=""
                        style="cursor: pointer; width: 15px; height: auto"
                        id="showPasswordConfirmation"
                      />
                    </div>
                    <span
                      class="invalid-feedback"
                      v-if="validation.password_confirmation.status"
                      role="alert"
                    >
                      <strong>{{
                        validation.password_confirmation.message
                      }}</strong>
                    </span>
                  </div>
                </div>
              </div>

              <div class="auth-submit row d-flex justify-content-between">
                <div class="col-lg-4 col-sm-12">
                  <router-link to="/login" style="color: #34b1e8"
                    >Kembali</router-link
                  >
                </div>
                <div class="col-lg-8 col-sm-12">
                  <button
                    type="submit"
                    id="submit"
                    class="btn btn-primary w-100"
                    style="border-radius: 30px"
                  >
                    Registrasi
                  </button>
                </div>
              </div>
            </form>
          </div>
        </div>
        <div class="divider"></div>
        <div class="mt-3 d-flex justify-content-center">
          <p style="color: #34b1e8">
            Sudah punya akun?
            <router-link to="/login"><b>Login</b></router-link>
          </p>
        </div>
      </div>
    </div>
  </div>
</template>
<script>
import axios from "axios";
import * as Api from "../../helper/Api.js";
export default {
  data() {
    return {
      data: {
        bidang_usaha: "",
        timezone: "",
      },
      bidang_usaha: [],
      validation: {
        email: {
          status: false,
          message: "",
        },
        nama_perusahaan: {
          status: false,
          message: "",
        },
        no_telp: {
          status: false,
          message: "",
        },
        bidang_usaha: {
          status: false,
          message: "",
        },
        total_anggota: {
          status: false,
          message: "",
        },
        password: {
          status: false,
          message: "",
        },
        password_confirmation: {
          status: false,
          message: "",
        },
      },
    };
  },
  created() {
    this.bidangUsaha();
  },
  mounted() {
    $(document).ready(function () {
      $("#showPassword").on("click", function () {
        var x = document.getElementById("password");
        var img = document.getElementById("showPassword");
        let svg = img.src;
        if (x.type === "password") {
          x.type = "text";
          svg = svg.replace("eye.svg", "eye-crossed.svg");
        } else {
          x.type = "password";
          svg = svg.replace("eye-crossed.svg", "eye.svg");
        }
        img.src = svg;
      });
      $("#showPasswordConfirmation").on("click", function () {
        var x = document.getElementById("password_confirmation");
        var img = document.getElementById("showPasswordConfirmation");
        let svg = img.src;
        if (x.type === "password") {
          x.type = "text";
          svg = svg.replace("eye.svg", "eye-crossed.svg");
        } else {
          x.type = "password";
          svg = svg.replace("eye-crossed.svg", "eye.svg");
        }
        img.src = svg;
      });
    });
  },
  methods: {
    validator(key, data) {
      this.validation[key] = data;
    },
    bidangUsaha() {
      axios.get(env.VITE_API_URL + "index-company-industri").then((resp) => {
        this.bidang_usaha = resp.data.data;
      });
    },
    register() {
      $("#submit").prop("disabled", true);
      $("#submit").html("Loading...");
      axios
        .post(env.VITE_API_URL + "register", this.data)
        .then((response) => {
          if (response.data.message) {
            if (Api.response(response.data) === Api.STATUS_SUCCESS) {
              this.$router.push("/login");
            }
          } else {
            let err = response.data;
            for (var key in err) {
              if (err.hasOwnProperty(key)) {
                this.validator(key, {
                  status: true,
                  message: err[key][0],
                });
              }
            }
          }
          $("#submit").prop("disabled", false);
          $("#submit").html("Registrasi");
        })
        .catch((error) => {
          $("#submit").prop("disabled", false);
          $("#submit").html("Registrasi");
        });
    },
  },
};
</script>
