            // console.log(response.data.data);
          }
        })
        .catch((e) => {
          this.$Progress.fail();
          Api.messageError(e);
        });
    },
    async loadChart2() {
      this.$Progress.start();
      await axios
        .get(env.VITE_API_URL + "chart2")
        .then((response) => {
          if (Api.response(response.data, false) === Api.STATUS_SUCCESS) {
            this.$Progress.finish();
            // console.log(response.data.data);
            this.chart2.chartOptions = {
              xaxis: {
                categories: response.data.data.month,
              },
            };
            this.chart2.series = [
              {
                name: "Terlambat",
                data: response.data.data.terlambat,
              },
              {
                name: "Tidak Terlambat",
                data: response.data.data.tidak_terlambat,
              },
            ];
            // console.log(response.data.data);
          }
        })
        .catch((e) => {
          this.$Progress.fail();
          Api.messageError(e);
        });
    },
    getNow: function () {
      const today = new Date();
      const hours =
        today.getHours().toString().length == 2
          ? today.getHours()
          : "0" + today.getHours();
      const minutes =
        today.getMinutes().toString().length == 2
          ? today.getMinutes()
          : "0" + today.getMinutes();
      const seconds =
        today.getSeconds().toString().length == 2
          ? today.getSeconds()
          : "0" + today.getSeconds();
      const time = hours + ":" + minutes + ":" + seconds;
      // const dateTime = date + " " + time;
      const dateTime = time;
      this.timestamp = dateTime;
    },
    convertDate(date) {
      return Api.convertDate(date, "DD-MM-YYYY hh:mm");
    },
  },
};
</script>