          if (Api.response(response.data) === Api.STATUS_SUCCESS) {
            this.data = {
              old_password: "",
              new_password: "",
              new_password_confirm: "",
              captcha: ""
            }
            $("#submit").prop("disabled", false);
            $("#submit").html("Submit");
            // this.$router.push("/detail-company-user/" + this.id);
